import axios from 'axios';

let user = null;

// Function to set user data in localStorage
function setUser(userData) {
  localStorage.setItem('user', JSON.stringify(userData));
}

// Function to remove user data from localStorage
function removeUser() {
  localStorage.removeItem('user');
}

// Function to retrieve user data from localStorage
function getUser() {
  const userData = localStorage.getItem('user');
  return userData ? JSON.parse(userData) : null;
}

function setTokens(tokens) {
  localStorage.setItem('user', JSON.stringify(userData));

}


// Function to perform logout API call
export async function logout() {
  try {
    await axios.post('/logout');
    removeUser();
  } catch (error) {
    console.error('Logout failed:', error);
  }
}

// Function to perform refresh token API call
export async function refreshToken() {
  try {
    const response = await axios.post('/refresh-token');
    const { accessToken, refreshToken } = response.data;
    const currentUser = getUser();
    setUser({ ...currentUser, accessToken, refreshToken });
  } catch (error) {
    console.error('Refresh token failed:', error);
    throw error; // Propagate the error for interceptor to handle
  }
}

// Function to get current user data
export function getCurrentUser() {
  if (!user) {
    user = getUser();
  }
  return user;
}
